{"server_name": "filesystem", "tool_name": "write_file", "params": {"path": "/root/code/middleware.ts", "content": "import { NextRequest, NextResponse } from 'next/server';
import jwt from 'jsonwebtoken';

// JWT secret key - in production, this should come from environment variables
const JWT_SECRET = process.env.JWT_SECRET || 'your-secret-key';

// Routes that require authentication
const protectedRoutes = [
  '/dashboard',
  '/profile',
  '/admin',
  '/api/protected'
];

// Routes that should redirect authenticated users (like login/register pages)
const authRoutes = ['/login', '/register'];

/**
 * Verifies if a JWT token is valid
 * @param token - JWT token string
 * @returns boolean indicating if token is valid
 */
function verifyToken(token: string): boolean {
  try {
    jwt.verify(token, JWT_SECRET);
    return true;
  } catch (error) {
    console.error('JWT verification failed:', error);
    return false;
  }
}

/**
 * Checks if the current path requires authentication
 * @param pathname - Current request pathname
 * @returns boolean indicating if path is protected
 */
function isProtectedRoute(pathname: string): boolean {
  return protectedRoutes.some(route => 
    pathname.startsWith(route)
  );
}

/**
 * Checks if the current path is an auth route (login/register)
 * @param pathname - Current request pathname
 * @returns boolean indicating if path is an auth route
 */
function isAuthRoute(pathname: string): boolean {
  return authRoutes.some(route => 
    pathname.startsWith(route)
  );
}

/**
 * Next.js middleware function
 * @param request - NextRequest object
 * @returns NextResponse object
 */
export function middleware(request: NextRequest) {
  const { pathname } = request.nextUrl;

  // Get JWT token from cookies
  const token = request.cookies.get('token')?.value || 
                request.cookies.get('jwt')?.value || 
                request.cookies.get('auth-token')?.value;

  // Check if user is authenticated
  const isAuthenticated = token ? verifyToken(token) : false;

  // If accessing protected route without valid token, redirect to login
  if (isProtectedRoute(pathname) && !isAuthenticated) {
    const loginUrl = new URL('/login', request.url);
    // Add redirect parameter to return user to intended page after login
    loginUrl.searchParams.set('redirect', pathname);
    return NextResponse.redirect(loginUrl);
  }

  // If accessing auth routes (login/register) while authenticated, redirect to dashboard
  if (isAuthRoute(pathname) && isAuthenticated) {
    const dashboardUrl = new URL('/dashboard', request.url);
    return NextResponse.redirect(dashboardUrl);
  }

  // For API routes, return 401 instead of redirect
  if (pathname.startsWith('/api/') && isProtectedRoute(pathname) && !isAuthenticated) {
    return NextResponse.json(
      { error: 'Unauthorized', message: 'Valid JWT token required' },
      { status: 401 }
    );
  }

  // Continue with the request if no redirect is needed
  return NextResponse.next();
}

// Configure which paths the middleware should run on
export const config = {
  matcher: [
    /*
     * Match all request paths except for the ones starting with:
     * - _next/static (static files)
     * - _next/image (image optimization files)
     * - favicon.ico (favicon file)
     * - public folder files
     */
    '/((?!_next/static|_next/image|favicon.ico|public/).*)',
  ],
};

// Export types for better TypeScript support
export interface JWTPayload {
  userId: string;
  email: string;
  role?: string;
  iat?: number;
  exp?: number;
}

// Helper function to decode JWT payload (without verification)
export function decodeTokenPayload(token: string): JWTPayload | null {
  try {
    return jwt.decode(token) as JWTPayload;
  } catch (error) {
    console.error('Failed to decode JWT payload:', error);
    return null;
  }
}

// Helper function to check if token is expired
export function isTokenExpired(token: string): boolean {
  const payload = decodeTokenPayload(token);
  if (!payload || !payload.exp) return true;

  const currentTime = Math.floor(Date.now() / 1000);
  return payload.exp < currentTime;
}"}}

